export const currentTime = () => {
  const now = new Date();
  return {
    time: `${now.getHours()}:${now.getMinutes()}`,
    date: `${now.getFullYear()}/${now.getMonth() + 1}/${now.getDate()}`,
    day: `${now.getDay()}`,
  };
};

export const pad = (x: number) => (x < 10 ? `0${x}` : `${x}`);
